import { Component } from '@angular/core';
import { Product } from './product';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent {
  title = 'Ürün Listesi';
  products: Product[] = [
    {id: 1, name: "Laptop", price: 2500, categoryId: 1, description: "Asus ZenBook", imageUrl:"https://cdn.vatanbilgisayar.com/Upload/PRODUCT/asus/thumb/136526-4_large.jpg"},
    {id: 2, name: "Mouse", price: 25, categoryId: 2, description: "A4 Tech", imageUrl:"https://www.aksesu.com/image/cache/catalog/shopping/elektronik-alet-aksesuarlari/bilgisayar-bilesenleri/giris-aygitlari/fareler-ve-izleme-toplari/a4-tech-xl-750bh-lazer-mouse-0-1000x1000.png"}
  ];
  deger: boolean = false;
}
<div class="row">
  <h3 *ngIf="deger; else noProduct">{{ title }}</h3>
  <ng-template #noProduct>
    <div class="alert alert-info" role="alert" style="display: inline block;">Bu kategoride ürün yoktur.</div>
  </ng-template>
  <div *ngFor="let product of products" class="card" style="width: 18rem">
    <img src="{{product.imageUrl}}" class="card-img-top" alt="...">
    <div class="card-body">
      <h5 class="card-title">{{ product.name }}</h5>
      <p class="card-text">
        {{ product.description }}₺
      </p>
      <a href="#" class="btn btn-primary">Satın Al!</a>
    </div>
  </div>
</div>
