import { Component } from '@angular/core';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent {
  title = 'Ürün Listesi';
  products: any[] = [
    {id: 1, name: "Laptop", price: "2500", categoryId: 1, description: "Asus ZenBook"},
    {id: 2, name: "Mouse", price: "25", categoryId: 2, description: "A4 Tech"}
  ];
  deger: boolean = false;
}
<div class="row">
  <h3 *ngIf="deger; else noProduct">{{ title }}</h3>
  <ng-template #noProduct>
    <div class="alert alert-info" role="alert" style="display: inline block;">Bu kategoride ürün yoktur.</div>
  </ng-template>
  <div class="card" style="width: 18rem">
    <img src="..." class="card-img-top" alt="..." />
    <div class="card-body">
      <h5 class="card-title">Card title</h5>
      <p class="card-text">
        Some quick example text to build on the card title and make up the bulk
        of the card's content.
      </p>
      <a href="#" class="btn btn-primary">Go somewhere</a>
    </div>
  </div>
</div>
